head.script:
var parser = new Fleur.DOMParser();
function escapeJS(text) {
	if (!text) {
		return "";
	}
	if (typeof(text) === "string") {
		text = text.replace(/\\/gm, "\\\\");
		text = text.replace(/\t/gm, "\\t");
		text = text.replace(/\n/gm, "\\n");
		text = text.replace(/\r/gm, "\\r");
		text = text.replace(/\"/gm, "\\\"");
	}
	return text;
}
function xqeval(testname, envfile, xexpr) {
    var cb = function(buf) {
		var xmldoc = parser.parseFromString(buf, "application/xml");
		var nsResolver = function(element) {
			return {
				defaultNamespace: element.getAttribute("xmlns"),
				nsresolver: element.ownerDocument.createNSResolver(element),
				lookupNamespaceURI: function(prefix) {
					if (prefix === "_") {
						return this.defaultNamespace;
					}
					return this.nsresolver.lookupNamespaceURI(prefix);
				}
			};
		};
		try {
			xmldoc.evaluate(xexpr, xmldoc, nsResolver(xmldoc.documentElement), Fleur.XPathResult.ANY_TYPE, null).then(
				function(res) {
					XsltForms_xmlevents.dispatch(XsltForms_globals.defaultModel, "evaluate-response", null, null, null, null,
						{
							testname: testname,
							result: res.toXQuery()
						});
				},
				function(err) {
				}
			);
		} catch(e) {
			XsltForms_xmlevents.dispatch(XsltForms_globals.defaultModel, "evaluate-response", null, null, null, null,
				{
					testname: testname,
					result: "Exception!\n" + e.stack
				});
		}
	};
	if (!envfile.endsWith("/")) {
		var getp = new Promise(function(resolve, reject) {
			var req = new XMLHttpRequest();
			req.open('GET', envfile, true);
			req.onload = function() {
				if (req.status === 200) {
					resolve(req.responseText);
				} else {
					reject("Not loaded!");
		      	}
			};
			req.send(null);
		});
		getp.then(
			function(xmlBuf) {
				cb(xmlBuf);
			},
			function(s) {
				XsltForms_xmlevents.dispatch(XsltForms_globals.defaultModel, "evaluate-response", null, null, null, null,
					{
						testname: testname,
						result: s
					});
			}
		);
	} else {
		cb("\x3cdummy/\x3e");
	}
}
